Program Perkalian;
\ 		Uses CRT;
 		Const phi = 3.14;
 		Var x : Integer;
 				y : Real;
 		Begin
 				ClrScr;
 				x := 5;
 				y := x * phi;
 				Writeln('Nilai y = ', y:5:2);
 				Readln;
 		End.

Program PembandingAngka;

 		Uses CRT;
 		Var a, b : Integer;


		Begin
 				ClrScr;
 				a := 10;
 				b := 6;
 				if (a > b) then
 						Writeln('Bilangan A terbesar')
 				else
 						Writeln('Bilangan B terbesar');
 		end.

Program JumlahGanjilGenap;

 Uses CRT;
 var i : Byte;
 		ganjil, genap : Byte;
 Begin
 		Clrscr;
 		ganjil := 0;
 		genap := 0;
 		For i:= 1 to 100 do
 		Begin
 				if (i mod 2)=0 Then
 						Inc(genap)
 				Else
 						Inc (ganjil);
 		End;
 		Writeln('Jumlah bilangan genap : ',genap);
 		Writeln('Jumlah bilangan ganjil : ',ganjil);
 		Readln;
 End.

Program Modulus;

 Uses CRT;
 var i : Byte;
 Begin
 		Clrscr;
 		for i:=1 to 10 do
 				Writeln(i,' mod 3 = ', i mod 3);
 		Readln;
 End.

Program CetakGanjil;

 Uses CRT;
 Var i : Byte;
 Begin
 		ClrScr;
 		i:=10;
 		while (i >= 1) Do
 		Begin
 				If i mod 2 = 1 Then
 				Begin
 				Write(i,' ');
 				if i= 5 Then
 					exit
 				End;
 				Dec(i);
 		End;
 		Readln;
 End.

Program Penjumlahan;

 Uses CRT;
 Var
 		y : Integer;
 		p : Real;
 		X : Real;
 Begin
 		y := 5;
 		p := 6;
 		x := y + p;
 		Writeln('Nilai X adalah :',x:4:1);
 End.

Program ContohLogic;

 Uses CRT;
 Var a,b,c,d : Boolean;
 Begin
 		a := True;
 		b := False;
 		c := not a;
 		d := not b;
 		Writeln(a);
 		Writeln(b);
 		Writeln(c);
 		Writeln(d);
 End.
